%% Import data from text file.
% Auto-generated by MATLAB

%% Initialize variables.
filename = '../airline_passengers_total_1996-2014.csv';
delimiter = ',';
startRow = 4;
endRow = 231;

%% Format string for each line of text:
%   column3: double (%f)
% For more information, see the TEXTSCAN documentation.
formatSpec = '%*s%*s%f%[^\n\r]';

%% Open the text file.
fileID = fopen(filename,'r');

%% Read columns of data according to format string.
% This call is based on the structure of the file used to generate this
% code. If an error occurs for a different file, try regenerating the code
% from the Import Tool.
textscan(fileID, '%[^\n\r]', startRow-1, 'WhiteSpace', '', 'ReturnOnError', false);
dataArray = textscan(fileID, formatSpec, endRow-startRow+1, 'Delimiter', delimiter, 'ReturnOnError', false);

%% Close the text file.
fclose(fileID);

%% Allocate imported array to column variable names
x = dataArray{:, 1};

%% Clear temporary variables
clearvars filename delimiter startRow endRow formatSpec fileID dataArray ans;


%% Forecasting routine according to http://mathworks.com/help/econ/forecast-airline-passenger-counts.html

m = 18;
N_f = 6;
N = numel(x);

x_hat = zeros(N + N_f, 1);
t = zeros(N,1);

for j=2 * m - 1 : N
    display(['Processing sample #' num2str(j)]);
    x_j = x(j- 2 * m + 2: j);
    
    tic
    Mdl = arima('Constant', 0, 'D', 1, 'Seasonality', 12, 'MALags', 1, 'SMALags', 12);
    EstMdl = estimate(Mdl,x_j);
    [yF] = forecast(EstMdl, N_f, 'Y0', x_j);
    t(j) = toc;
    
    if j == 2 * m - 1
       x_hat(j+1 : j+6) = yF;
    else
       x_hat(j+6)=yF(end);
    end
end

save('predictions_new_arima.mat','x_hat','t')