%% Import data from text file.
% Auto-generated by MATLAB

%% Initialize variables.
filename = '../AirPassengers.csv';
delimiter = ',';
startRow = 2;

%% Format string for each line of text:
%   column2: double (%f)
% For more information, see the TEXTSCAN documentation.
formatSpec = '%*s%f%[^\n\r]';

%% Open the text file.
fileID = fopen(filename,'r');

%% Read columns of data according to format string.
% This call is based on the structure of the file used to generate this
% code. If an error occurs for a different file, try regenerating the code
% from the Import Tool.
dataArray = textscan(fileID, formatSpec, 'Delimiter', delimiter, 'HeaderLines' ,startRow-1, 'ReturnOnError', false);

%% Close the text file.
fclose(fileID);

%% Post processing for unimportable data.
% No unimportable data rules were applied during the import, so no post
% processing code is included. To generate code which works for
% unimportable data, select unimportable cells in a file and regenerate the
% script.

%% Allocate imported array to column variable names
x = dataArray{:, 1};


%% Clear temporary variables
clearvars filename delimiter startRow formatSpec fileID dataArray ans;

%% Forecasting routine according to http://mathworks.com/help/econ/forecast-airline-passenger-counts.html

m = 18;
N_f = 6;
N = numel(x);

x_hat = zeros(N + N_f, 1);
t = zeros(N,1);

for j=2 * m - 1 : N
    display(['Processing sample #' num2str(j)]);
    x_j = x(j- 2 * m + 2: j);
    
    tic
    Mdl = arima('Constant', 0, 'D', 1, 'Seasonality', 12, 'MALags', 1, 'SMALags', 12);
    EstMdl = estimate(Mdl,x_j);
    [yF] = forecast(EstMdl, N_f, 'Y0', x_j);
    t(j) = toc;
    
    if j == 2 * m - 1
       x_hat(j+1 : j+6) = yF;
    else
       x_hat(j+6)=yF(end);
    end
end

save('predictions_old_arima.mat','x_hat','t')